da;        session_write_close();}?><html>    <head>        <title>:: Intranet :: Bancos</title>        <meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1" />        <link rel="shortcut icon" href="../../favicon.ico" />        <link href="../../net1.css" rel="stylesheet" type="text/css" />        <link href="bancos.css" rel="stylesheet" type="text/css" />        <link href="../../css/cupertino/jquery-ui-1.9.2.custom.css" rel="stylesheet" type="text/css" />        <link href="../../css/validationEngine.jquery.css" rel="stylesheet" type="text/css" />                <script src="../../js/jquery-1.8.3.min.js" type="text/javascript"></script>        <script src="../../js/jquery-ui-1.9.2.custom.min.js" type="text/javascript"></script>        <script src="../../js/global.js" type="text/javascript"></script>        <script src="../../jquery/mascara/jquery.maskedinput-1.2.2.js" type="text/javascript" ></script>        <script src="../../js/jquery.validationEngine.js" type="text/javascript"></script>        <script src="../../js/jquery.validationEngine-pt.js" type="text/javascript"></script>                <script>            $(function() {                //mascara                                $("#tel").mask("(99)9999-9999?9");                                //validation engine                $("#form1").validationEngine({promptPosition : "topRight"});            });        </script>                <style>            .data{                width: 80px;            }            .colEsq{                float: left;                width: 57%;                margin-top: -10px;            }            fieldset{                margin-top: 10px;                            }            fieldset legend{                font-family: 'Exo 2', sans-serif;                font-size: 16px!important;                font-weight: bold;            }            .first{                vertical-align: 0!important;            }