<?phpclass Abastecimento{        public function getAbastecimento($id_regiao) {        $anual = $_REQUEST['anual'];        $dataIni = ($_REQUEST['data_ini'] != "") ? converteData($_REQUEST['data_ini']) : "";        $dataFim = ($_REQUEST['data_fim'] != "") ? converteData($_REQUEST['data_fim']) : "";                if($anual == 1){            $select = 'YEAR(A.data_libe)  = '.date('Y');        }else{            $select = 'A.data_libe BETWEEN \''.$dataIni.'\' AND \''.$dataFim.'\'';        }        $result = "SELECT A.id_combustivel, B.nome1 AS autorizado_para, C.placa, DATE_FORMAT(A.data_libe, '%d/%m/%Y') AS autorizado_em, D.nome1 AS autorizado_por, A.numero AS num_nota, A.valor            FROM fr_combustivel AS A            LEFT JOIN funcionario AS B ON(B.id_funcionario = A.id_user)            LEFT JOIN fr_carro AS C ON(C.id_carro = A.id_carro)            LEFT JOIN funcionario AS D ON(D.id_funcionario = A.user_libe)            WHERE A.status_reg = 2 AND A.id_regiao = {$id_regiao} AND {$select}            ORDER BY A.data_libe ASC";        $abastecimento = mysql_query($result) or die(mysql_error());        return $abastecimento;    }        public function getAbastecimentoIndividual($id_regiao) {        $anual = $_REQUEST['anual'];        $dataIni = ($_REQUEST['data_ini'] != "") ? converteData($_REQUEST['data_ini']) : "";        $dataFim = ($_REQUEST['data_fim'] != "") ? converteData($_REQUEST['data_fim']) : "";                if($anual == 1){            $select = 'YEAR(A.data_libe)  = '.date('Y');        }else{            $select = 'A.data_libe BETWEEN \''.$dataIni.'\' AND \''.$dataFim.'\'';        }        $result = "SELECT B.nome, SUM(A.valor) AS tot_individual            FROM fr_combustivel AS A            LEFT JOIN funcionario AS B ON(B.id_funcionario = A.id_user)            WHERE A.status_reg = 2 AND A.id_regiao = {$id_regiao} AND {$select}            GROUP BY A.id_user            ORDER BY B.nome";        $abastecimento = mysql_query($result) or die(mysql_error());        return $abastecimento;    }        public function getRegiaoAb($id_regiao) {        $sql = "SELECT *                FROM regioes                            WHERE id_regiao = '{$id_regiao}'";        $res = mysql_query($sql) or die(mysql_error());        $row = mysql_fetch_assoc($res);        return $row;    }        public function getAbastecimentoAPagar($id_regiao, $id_combustivel) {        $auxCombustivel = (!empty($id_combustivel)) ? " AND A.id_combustivel = $id_combustivel " : '';        $result = "        SELECT             A.id_combustivel, A.nome, A.funcionario, A.id_user, date_format(A.data, '%d/%m/%Y')as data, A.id_regiao, A.destino, A.valor,            B.nome nomeFuncionario,            C.regiao nomeRegiao        FROM fr_combustivel AS A        LEFT JOIN funcionario AS B ON(B.id_funcionario = A.id_user)        LEFT JOIN regioes AS C ON(C.id_regiao = A.id_regiao)        WHERE A.status_reg = 1 AND A.id_regiao = {$id_regiao} $auxCombustivel        ORDER BY B.nome";        $abastecimento = mysql_query($result) or die(mysql_error());        if(empty($id_combustivel)){            while($rowAbastecimento = mysql_fetch_assoc($abastecimento)){                $dados[$rowAbastecimento[id_combustivel]] = $rowAbastecimento;            }        } else {            $dados = mysql_fetch_assoc($abastecimento);        }        return $dados;    }}?>