<?phpclass ProcessosJuridicosClass {        public static function getAudienciaHoje() {        $usuario = carregaUsuario();        $sql = mysql_query("SELECT B.*, IF(andamento_data_movi = (            SELECT MAX(andamento_data_movi)            FROM proc_trab_andamento            WHERE proc_id = A.proc_id),'1', '0') AS ultimo_andamento, MONTH(andamento_data_movi) AS mes, YEAR(andamento_data_movi) AS ano            FROM processos_juridicos AS A            INNER JOIN proc_trab_andamento AS B ON A.proc_id = B.proc_id            INNER JOIN regioes AS C ON(A.id_regiao = C.id_regiao)            INNER JOIN master AS D ON(C.id_master = D.id_master)            WHERE A.status = 1 AND andamento_status = 1 AND D.id_master = {$usuario['id_master']} AND proc_status_id NOT IN(1,2) AND andamento_data_movi = CURRENT_DATE()            HAVING ultimo_andamento = 1            ORDER BY B.andamento_data_movi DESC") or die(mysql_error());                                       $tot = mysql_num_rows($sql);                return $tot;    }        public static function getAudienciaAmanha() {        $usuario = carregaUsuario();        $sql = mysql_query("SELECT B.*, IF(andamento_data_movi = (            SELECT MAX(andamento_data_movi)            FROM proc_trab_andamento            WHERE proc_id = A.proc_id),'1', '0') AS ultimo_andamento, MONTH(andamento_data_movi) AS mes, YEAR(andamento_data_movi) AS ano            FROM processos_juridicos AS A            INNER JOIN proc_trab_andamento AS B ON A.proc_id = B.proc_id            INNER JOIN regioes AS C ON(A.id_regiao = C.id_regiao)            INNER JOIN master AS D ON(C.id_master = D.id_master)            WHERE A.status = 1 AND andamento_status = 1 AND D.id_master = {$usuario['id_master']} AND proc_status_id NOT IN(1,2) AND andamento_data_movi = DATE_ADD(CURRENT_DATE(), INTERVAL 1 DAY)            HAVING ultimo_andamento = 1            ORDER BY B.andamento_data_movi DESC") or die(mysql_error());                                       $tot = mysql_num_rows($sql);                return $tot;    }        public static function getAudienciaMes() {        $usuario = carregaUsuario();        $sql = mysql_query("SELECT B.*, IF(andamento_data_movi = (            SELECT MAX(andamento_data_movi)            FROM proc_trab_andamento            WHERE proc_id = A.proc_id),'1', '0') AS ultimo_andamento, MONTH(andamento_data_movi) AS mes, YEAR(andamento_data_movi) AS ano            FROM processos_juridicos AS A            INNER JOIN proc_trab_andamento AS B ON A.proc_id = B.proc_id            INNER JOIN regioes AS C ON(A.id_regiao = C.id_regiao)            INNER JOIN master AS D ON(C.id_master = D.id_master)            WHERE A.status = 1 AND andamento_status = 1 AND D.id_master = {$usuario['id_master']} AND proc_status_id NOT IN(1,2) AND andamento_data_movi > DATE_ADD(CURRENT_DATE(), INTERVAL 1 DAY) AND MONTH(andamento_data_movi) = MONTH(CURRENT_DATE)            HAVING ultimo_andamento = 1            ORDER BY B.andamento_data_movi DESC") or die(mysql_error());                                       $tot = mysql_num_rows($sql);                return $tot;    }    }?>