<?phpclass Reembolso{        public function getReembolso() {        $usuario = carregaUsuario();        $id_user = $usuario['id_funcionario'];        $sql = "SELECT *, DATE_FORMAT(A.data, '%d/%m/%Y') AS data, A.nome AS nome_ree, B.nome AS nome_fun, A.status AS status                FROM fr_reembolso AS A                /*LEFT JOIN funcionario AS B ON (A.id_user = B.id_funcionario)*/                LEFT JOIN rh_clt AS B ON (A.id_user = B.id_clt)                WHERE A.id_usercad = '{$id_user}'";        $reemb = mysql_query($sql) or die(mysql_error());        return $reemb;    }    public function getFuncionarioId($id_fun){        $sql = "SELECT *                FROM funcionario                WHERE id_funcionario = '{$id_fun}'";        $fun = mysql_query($sql) or die(mysql_error());        $row = mysql_fetch_assoc($fun);        return $row;    }    public function getCltId($id){        $sql = "SELECT *                FROM rh_clt                WHERE id_clt = '{$id}'";        $fun = mysql_query($sql) or die(mysql_error());        $row = mysql_fetch_assoc($fun);        return $row;    }    public function getFuncionariosR() {        $sql = mysql_query("SELECT *                            FROM funcionario                            WHERE status_reg = '1'                            ORDER BY nome1");        $func = array("-1" => "Ф Selecione ╗");        while ($rst = mysql_fetch_assoc($sql)) {        $func[$rst['id_funcionario'].'///'.$rst['nome1']] = $rst['nome1'];        }        return $func;    }        public function getCltR() {        $sql = mysql_query("SELECT id_clt, nome                            FROM rh_clt                            WHERE status_reg = '1' AND status NOT IN (SELECT codigo FROM rhstatus A WHERE A.tipo = 'recisao')                            ORDER BY nome");        $func = array("-1" => "Ф Selecione ╗");        while ($rst = mysql_fetch_assoc($sql)) {        $func[$rst['id_clt'].'///'.$rst['nome']] = $rst['nome'];        }        return $func;    }    public function cadReembolso() {        $usuario = carregaUsuario();        $regiao = $usuario['id_regiao'];        $id_user = $usuario['id_funcionario'];        $funcionario = $_REQUEST['funcionario'];            $valor = $_REQUEST['valor'];        $descricao = acentoMaiusculo($_REQUEST['descricao']);        $agencia = $_REQUEST['agencia'];        $conta = $_REQUEST['conta'];        $nomefavo = $_REQUEST['nomefavo'];        $cpf = $_REQUEST['cpf'];        $banco = $_REQUEST['banco'];        $dataT = date('Y-m-d H:i:s');            $data_cad = date('Y-m-d');        if($funcionario == 1){            $user_exp = explode("///", $_REQUEST['user']);            $user = $user_exp[0];            $nome = acentoMaiusculo($user_exp[1]);        }else{                    $nome = $_REQUEST['nome_razao'];        }        $valorF = str_replace(".","",$valor);        $valorF = str_replace(",",".",$valorF);        if (($nome == '')) {            $_SESSION['MESSAGE'] = "Verifique o campo Nome, ele nсo pode ser vazio";            $_SESSION['MESSAGE_COLOR'] = 'message-red';        } else {            $insere_reembolso = mysql_query("INSERT INTO fr_reembolso (id_regiao,id_usercad,data_cad,funcionario,id_user,nome,valor, DATA,descricao,banco,agencia,conta,favorecido,cpf) VALUES                 ('{$regiao}','{$id_user}','{$data_cad}', '{$funcionario}', '{$user}','{$nome}', '{$valorF}', '{$dataT}', '{$descricao}', '{$banco}', '{$agencia}', '{$conta}', '{$nomefavo}', '{$cpf}')")                 or die(mysql_error());            $reembolso = mysql_insert_id();            //dados usuario para cadastro de log            $local = "Financeiro - Reembolso";            $ip = $_SERVER['REMOTE_ADDR'];            $acao = "Reebolso Solicitado: ID{$reembolso}";            $id_usuario = $usuario['id_funcionario'];            $tipo_usuario = $usuario['tipo_usuario'];            $grupo_usuario = $usuario['grupo_usuario'];            $regiao = $usuario['id_regiao'];            $insere_log = mysql_query("INSERT INTO log (id_user, id_regiao, tipo_user, grupo_user, local, horario, ip, acao) VALUES                ('{$id_usuario}', '{$regiao}', '{$tipo_usuario}', '{$grupo_usuario}', '{$local}', NOW(), '{$ip}', '{$acao}')") or die(mysql_error());                              if ($insere_reembolso && $insere_log) {                $_SESSION['MESSAGE'] = 'Informaушes gravadas com sucesso!';                $_SESSION['MESSAGE_COLOR'] = 'message-blue';                $_SESSION['MESSAGE_TYPE'] = 'info';                $_SESSION['regiao'] = $regiao;                $_SESSION['id_reembolso'] = $reembolso;                session_write_close();                header("refresh: 5;doc_reembolso.php");            } else {                $_SESSION['MESSAGE'] = 'Erro ao cadastrar o reembolso '.$reembolso;                $_SESSION['MESSAGE_COLOR'] = 'message-red';                $_SESSION['regiao'] = $regiao;            }        }    }        public function getReembolsoAPagar($id_regiao) {        $result = "        SELECT             A.id_reembolso, A.nome, A.funcionario, A.id_user, date_format(data, '%d/%m/%Y %H:%i:%s')as data, A.id_regiao, A.valor,            B.nome nomeFuncionario,            C.regiao nomeRegiao        FROM fr_reembolso AS A        LEFT JOIN funcionario AS B ON(B.id_funcionario = A.id_user)        LEFT JOIN regioes AS C ON(C.id_regiao = A.id_regiao)        WHERE A.status = 1 AND A.id_regiao = {$id_regiao}        ORDER BY B.nome";        $reembolso = mysql_query($result) or die(mysql_error());        while($rowReembolso = mysql_fetch_assoc($reembolso)){            $dados[$rowReembolso['id_reembolso']] = $rowReembolso;        }        return $dados;    }}?>