<?phpinclude('../conn.php');include('../classes/EntradaClass.php');include('../wfunction.php');$entrada = new Entrada();if(isset($_REQUEST['method']) && $_REQUEST['method'] != ""){        $retorno = array("status" => 0);    if($_REQUEST['method'] == "carrega_notas"){        $id = $_REQUEST['id'];        $row = $entrada->getNotas($id);                        if($entrada->getParceiros($id)){            $retorno = array("status" => 1, "data_emissao" => $row['data_emi']);        }                echo json_encode($retorno);        exit();    }}?>