<?phpinclude('../conn.php');include('../classes/EntradaClass.php');include('../wfunction.php');$entrada = new Entrada();if(isset($_REQUEST['method']) && $_REQUEST['method'] != ""){        $retorno = array("status" => 0);    if($_REQUEST['method'] == "total_parceiros"){        $id_reg = $_REQUEST['id'];        $row = $entrada->getParceiros($id_reg);        $tot = mysql_num_rows($row);                if($row){            $retorno = array("status" => 1, "total" => $tot, "regiao" => $id_reg);        }                echo json_encode($retorno);        exit();    }}?>