z").mask("99.999.999/9999-99");                $("#cpf").mask("999.999.999-99");                $("#tel, #fax").mask("(99) 9999-99999");                $("#cod_municipio").mask("99-99999");                $("#natureza").mask("999-9");                $("#ano").mask("9999");                $("#data_inicio").mask("99/99/9999");                $("#cnae").mask("9999-9/99");                                $("#pat").change(function(){                    if($(this).prop("checked")){                        $(".pat_hide").show();                    }                    else {                        $(".pat_hide").hide();                    }                });                                $("#cpf").change(function(){                    if ($(this).val()) {                        if(!validaCpf($(this).val())){                            bootAlert('CPF inválido!',null,null,'danger');                            $('input[type=submit]').attr('disabled',true);                        }else{                            $('input[type=submit]').attr('disabled',false);                        }                    }                });                                $("#cnpj, #cnpj_matriz").change(function(){                    if ($(this).val()) {                        if(!validarCNPJ($(this).val())){                            bootAlert('CNPJ inválido!',null,null,'danger');                            $('input[type=submit]').attr('disabled',true);                        }else{                            $('input[type=submit]').attr('disabled',false);                        }                    }                });                                                                          //mascara monetaria                $("#capital").maskMoney({prefix:'R$ ', allowNegative: true, thousands:'.', decimal:','});                                //somente numeros                //$("#im, #ie, #acid_trabalho, #proprietarios, #familiares, #ano, #agencia, #conta, #p_empresa, #p_acid_trabalho, #p_prolabora, #p_terceiros, #p_filantropicas, #terceiros").keypress(somenteNumeros);                                //validation engine                $("#form1").validationEngine({promptPosition : "topRight"});                                $("#foto").on("click",function(){                    if($(this).prop("checked")){                        $(".p_logo2").removeClass("hidden");                        $("#arquivo").addClass("validate[required,custom[arquivoImg]]");                    }else{                        $("#arquivo").val('');                        $("#arquivo").removeClass("validate[required,custom[arquivoImg]]");                        $(".p_logo2").addClass("hidden");                    }                });                                //url                $("#site").focus(function(){                    if($(this).val() == ''){                        $(this).val('http://www.');                    }                });                $("#site").blur(function(){                    if($(this).val() == 'http://www.'){                        $(this).val('');                    }                });                                $("#cadastrar").click(function (){                    if(($("#site").val() != '')){                        $("#site").addClass("validate[custom[url]]");                    }                    if(($("#email").val() != '')){                        $("#email").addClass("validate[custom[email]]");                    }                    if(($("#data_inicio").val() != '')){                        $("#data_inicio").addClass("validate[custom[dateBr]]");                    }                });                                $('#pat').trigger('change');                                $('#form1').on('submit', function(e) {                    if ($('#pat').is(':checked')) {                        var pat_porc_serv_prop = parseInt($('#pat_porc_serv_prop').val()+0, 10);                        var pat_porc_adm_coz = parseInt($('#pat_porc_adm_coz').val()+0, 10);                        var pat_porc_ref_conv = parseInt($('#pat_porc_ref_conv').val()+0, 10);                        var pat_porc_ref_trans = parseInt($('#pat_porc_ref_trans').val()+0, 10);                        var pat_porc_ces_alim = parseInt($('#pat_porc_ces_alim').val()+0, 10);                        var pat_porc_alim_conv = parseInt($('#pat_porc_alim_conv').val()+0, 10);                        var patTotal = pat_porc_serv_prop + pat_porc_adm_coz + pat_porc_ref_conv + pat_porc_ref_trans + pat_porc_ces_alim + pat_porc_alim_conv;                        if(patTotal != 100){                            bootAlert("Os percentuais de PAT somam <strong>"+patTotal+"%</strong>, porém, devem somar exatamente 100%.", "Erro nos percentuais de PAT", null, 'warning');                            e.preventDefault();                            return false;                        }                    }                });            });        </script>    </body></html>